import { Component, OnInit, Output, EventEmitter } from '@angular/core'; // Importamos Output y EventEmitter
import { Observable, EMPTY } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { CifNode } from '../../core/models/Cif-code';
import { CifCodeService } from '../../core/services/cif-code.service';
import { NodeToggleEvent } from '../tree-node/tree-node.component'; // Importamos la interfaz

@Component({
  selector: 'app-cif-tree',
  templateUrl: './cif-tree.component.html',
  styleUrls: ['./cif-tree.component.css']
})
export class CifTreeComponent implements OnInit {

  public tree$: Observable<CifNode[]>;
  public errorMsg: string = '';

  // Este evento enviará la lista final al formulario (CalificacionComponent)
  @Output() selectionUpdate = new EventEmitter<CifNode[]>();

  // Usamos un Map para guardar los seleccionados por su 'codigo' (es más eficiente)
  private selectedNodes = new Map<string, CifNode>();

  constructor(private cifCodeService: CifCodeService) { }

  ngOnInit(): void {
    this.tree$ = this.cifCodeService.getTree().pipe(
      catchError(err => {
        this.errorMsg = 'Error al cargar los datos: ' + err.message;
        return EMPTY;
      })
    );
  }

  // Esta función se llamará CADA VEZ que un checkbox (en cualquier nivel) cambie
  public onNodeToggle(event: NodeToggleEvent): void {

    if (event.checked) {
      // Si está marcado, lo añadimos al Map
      this.selectedNodes.set(event.node.codigo, event.node);
    } else {
      // Si está desmarcado, lo borramos del Map
      this.selectedNodes.delete(event.node.codigo);
    }

    // Convertimos los valores del Map a un array
    const selectedList = Array.from(this.selectedNodes.values());

    // ¡Emitimos la lista completa hacia el componente padre!
    this.selectionChanged.emit(selectedList);
  }
}
